// importing the Java methods
import java.text.SimpleDateFormat;
import java.util.Date;

def DepartureDate = new Date().plus(3)
def ReturnDate = new Date().plus(6)
def ModifyDate = new Date().plus(6)


/* Perf prod*/ 
def origin = "YUL"
def destination = "NCE"


//Date and flight info
testRunner.testCase.setPropertyValue("DepartureDate", DepartureDate.format("yyyy-MM-dd"));
testRunner.testCase.setPropertyValue("ReturnDate", ReturnDate.format("yyyy-MM-dd"));
testRunner.testCase.setPropertyValue("ModifyDate", ModifyDate.format("yyyy-MM-dd"));
testRunner.testCase.setPropertyValue("Origin", origin);
testRunner.testCase.setPropertyValue("Destination", destination);


//Passenger Info
def FirstName = org.apache.commons.lang.RandomStringUtils.randomAlphabetic(6)
def LastName = org.apache.commons.lang.RandomStringUtils.randomAlphabetic(6)
def MiddleName = org.apache.commons.lang.RandomStringUtils.randomAlphabetic(6)
testRunner.testCase.setPropertyValue("p_FirstName", FirstName.toUpperCase().concat("ADT"));
testRunner.testCase.setPropertyValue("p_LastName", LastName.toUpperCase().concat("ADT"));
testRunner.testCase.setPropertyValue("p_MiddleName", MiddleName.toUpperCase().concat("ADT"));

import com.eviware.soapui.impl.wsdl.teststeps.WsdlTestRequestStep
//date time is appended to the file name
//def dt = new Date().format('yyyyMMdd_HHmmss')

def project = context.testCase.testSuite.project;
def suite = context.testCase.testSuite;
def kase = context.testCase
def tfolder = 'Feb_Regression'

//context.expand( '${#TestSuite#JIRA folder}' )

def dirpath = "C:/Download/${tfolder}/${kase.name}"
log.info(dirpath)
def Dir = new File(dirpath)
if(!Dir.exists()) Dir.mkdirs()

//log.info (kase);

for(def i=0; i < kase.testStepCount -1 ; i++)
{
log.info(i);
def step = kase.getTestStepAt(i).name
log.info(step)
if(kase.getTestStepAt(i).disabled == false && kase.getTestStepAt(i) instanceof WsdlTestRequestStep)
{
def filepath = "${dirpath}/${step}_reqres.xml"
def m = new File(filepath)
def str = '${' + step + '#RawRequest}'
def req = context.expand(str)
log.info(req)
m.write(req,"UTF-8")
def resp = '${' + step + '#Response}'
def res = context.expand( resp )
m.append("\n\n\n\n\n", "UTF-8")
m.append(res, "UTF-8")
}
}
